
public class Ex1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 45, y = 55, a = 30, b = 20;
		int averige = x + y + a + b;
		
		
		System.out.println("변수 x의 값은" + x);
		System.out.println("변수 y의 값은" + y);
		System.out.println("변수 a의 값은" + a);
		System.out.println("변수 b의 값은" + b);
		System.out.println("변수들의 합은" + (x + y + a + b));
		System.out.println("변수들의 평균은" + (averige/4));
	}

}
