package ch3;

public class Ex3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int	a = 82, b = 17, c = 0;
		
		c = a + b;
		System.out.println(c);	//100
		
		c = a - b;
		System.out.println(c);	//65
		
		c = a * b;
		System.out.println(c);	//1394

		c = a / b;
		System.out.println(c);	//4
	}

}
