package ch4;

public class Ex3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=9, b;
		b=a++;
		System.out.println("a="+ a + " b=" + b);
		
		b=++a;
		System.out.println("a="+ a + " b=" + b);
		
		b=a--;
		System.out.println("a="+ a + " b=" + b);
		
		b=--a;
		System.out.println("a="+ a + " b=" + b);
		
	}

}
